import './App.css';
import React, { useEffect } from 'react'
import Card from '@material-ui/core/Card'
import Grid from '@material-ui/core/Grid'
import InputForm from './Components/InputForm'
import logo from './assets/tilogo.png'
import { makeStyles } from '@material-ui/core/styles'
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom"
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { red } from '@material-ui/core/colors';
import TypeWriterEffect from 'react-typewriter-effect';

const useStyles = makeStyles((theme) => ({
  logo: {
    marginLeft: theme.spacing(15)
  },
  body: {
    padding: theme.spacing(10),
    paddingLeft: theme.spacing(90),
    paddingRight: theme.spacing(90)

  },
  title: {
    color: 'white',
    textAlign: 'center'
  }
}))


function App() {
  useEffect(() => {
    document.body.style.backgroundColor = '#1652F0'
  }, [])

  const classes = useStyles()
  const myRef = document.querySelector('.scrollable-div')

  return (
    <div>
      <img src={logo} width='200' height='200' className={classes.logo}/>
      <Grid className={classes.body}>
      <TypeWriterEffect
            textStyle={{ fontFamily: 'Red Hat Display', color: 'white', textAlign: 'center', margin: '50px' }}
            startDelay={100}
            cursorColor="white"
            text="This is a single text"
            typeSpeed={100}
            hideCursorAfterText={true}
            scrollArea={myRef}
          />
        <Box t={2}>
          <InputForm className={classes.form}/>
        </Box>
      </Grid>
    </div>
    );
}

export default App;
