import React, { useEffect, useState } from 'react'
import Box from '@material-ui/core/Box'
import Container from '@material-ui/core/Container'
import InputForm from '../Components/InputForm'
import { makeStyles } from '@material-ui/core/styles'
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom"
import { red } from '@material-ui/core/colors';
import TypeWriterEffect from 'react-typewriter-effect';
import { AnimateOnChange } from 'react-animation'

const useStyles = makeStyles((theme) => ({
    logo: {
      alignSelf: 'start'
    },
    body: {
      display: 'flex',
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column'
    },
    card: {
      display: 'flex',
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column'
    },
    title: {
      color: 'white',
      textAlign: 'center'
    },
    animateWrapper: {
      display: 'flex',
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
    }
  }))

const Landing = ({onSubmit}) => {
    const classes = useStyles()
    const myRef = document.querySelector('.scrollable-div')
    return (
    <AnimateOnChange>
      <div className={classes.body}>
        <Container className={classes.body}>
            <TypeWriterEffect
                textStyle={{ fontFamily: 'Red Hat Display', color: 'white', textAlign: 'center' }}
                startDelay={100}
                cursorColor="white"
                text="Predict. Research. Invest."
                typeSpeed={100}
                hideCursorAfterText={true}
                scrollArea={myRef}
                className={classes.title}
                />
                <Box pt={10} className={classes.card}>
                <InputForm className={classes.form} onSubmit={setPrediction} />
                </Box>
            </Container>
        </div>
    </AnimateOnChange>
    )
}

export default Landing
